 echo do_shortcode("[smartblock id=120]"); ?>     </div></div><div class="articles_section">    <div class="container">        <?php        if ($term) {            $term_obj = get_term_by("slug", $term, $tax_name);            ?>            <h2 class="section_title"> Learn more about <span class="rainbow"> <?php echo $term_obj->name; ?></span></h2>        <?php } else { ?>            <h2 class="section_title"> Learn more about <span class="rainbow"> Products</span></h2>        <?php } ?>        <div class=" owl-carousel owl-theme">            <?php            $args = array();            $args["post_type"] = "articles";            $args["page"] = 0;            $args["per_page"] = 12;            $results = gamma_get_posts($args);            foreach ($results["items"] as $item) {                ?>                <div class="item">                    <a href="<?php echo $item["post_permalink"]; ?>" class="no_decoration">                        <div class="article_box">                            <div class="category_type">                                <?php echo $item["tax_string_article_category"]; ?>                            </div>                            <div class="article_image">                                <?php echo $item["featured_img_medium"]; ?>                            </div>                            <div class="article_content">                                <h3><?php echo $item["post_title"]; ?></h3>                                 <p><?php echo strip_tags($item["post_excerpt"]); ?></p>                            </div>                            <hr />                            <div class="read_more_article">                                Read more <i class="fa fa-long-arrow-right" aria-hidden="true"></i>                            </div>                        </div>                    </a>                </div>            <?php } ?>        </div>    </div></div>